#include "types.h"
#include "stat.h"
#include "user.h"
#include "fcntl.h"


int main(int argc, char *argv[]){
    int sectors[200];
    int fd1, fd2, fd3;
    printf(1, "file sectors: \n");
    fd1 = open("a.txt", O_CREATE | O_RDWR);
    fd2 = open("b.txt", O_CREATE | O_RDWR);
    fd3 = open("c.txt", O_CREATE | O_RDWR);
    write(fd1, "", 2001);
    write(fd2, "", 2001);
    write(fd2, "", 2001);
    write(fd3, "", 2001);
    printf(1, "file descriptor: %d\n",atoi(argv[1]));
    int sizee1 = get_file_sectors(atoi(argv[1]), sectors);
    for (int i=0;i<sizee1;i++){
        printf(1, "%d\n", sectors[i]);
    }
    close(fd1);
    close(fd2);
    close(fd3);
    exit();
}